import { Component, OnInit, NgZone } from '@angular/core';
import { Router, ActivatedRoute } from '@angular/router';
import { CrudService } from '../../service/crud.service';
import { FormGroup, FormBuilder } from '@angular/forms';

@Component({
  selector: 'app-user-detail',
  templateUrl: './user-detail.component.html',
  styleUrls: ['./user-detail.component.scss'],
})
export class UserDetailComponent implements OnInit {
  getId: any;
  updateForm!: FormGroup;

  constructor(
    private formBuilder: FormBuilder,
    private router: Router,
    private ngZone: NgZone,
    private activatedRoute: ActivatedRoute,
    private crudService: CrudService
  ) {}

  ngOnInit(): void {
    this.getId = this.activatedRoute.snapshot.paramMap.get('id');

    this.updateForm = this.formBuilder.group({
      firstName: [''],
      lastName: [''],
      email: [''],
    });

    this.crudService.GetUser(this.getId).subscribe((res: any) => {
      this.updateForm.patchValue({
        firstName: res.firstName,
        lastName: res.lastName,
        email: res.email,
      });
    });
  }

  onUpdate(): void {
    this.crudService.updateUser(this.getId, this.updateForm.value).subscribe({
      next: () => {
        console.log('User updated');
        this.ngZone.run(() => this.router.navigate(['/users-list']));
      },
      error: (err) => console.error(err)
    });
  }
}
<div class="row justify-content-center mt-5">
  <div class="col-md-4">
    <form [formGroup]="updateForm" (ngSubmit)="onUpdate()">
      <div class="form-group">
        <label>First Name</label>
        <input
          class="form-control"
          type="text"
          formControlName="firstName"
          required
        />
      </div>

      <div class="form-group">
        <label>Last Name</label>
        <input
          class="form-control"
          type="text"
          formControlName="lastName"
          required
        />
      </div>

      <div class="form-group">
        <label>Email</label>
        <input
          class="form-control"
          type="text"
          formControlName="email"
          required
        />
      </div>
      <br>
      <div class="form-group">
        <button class="btn btn-primary btn-block" type="submit">Update</button>
        <button class="btn btn-secondary mb-3" [routerLink]="['/users-list']">← Retour</button>

      </div>
    </form>
  </div>
</div>
