import { Component } from '@angular/core';
import { LoginService } from '../service/login.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.scss']
})
export class RegisterComponent {
  username = '';
  password = '';
  confirmPassword = '';
  message = '';

  constructor(private loginService: LoginService, private router: Router) {}

  register() {
    if (this.password !== this.confirmPassword) {
      this.message = 'Les mots de passe ne correspondent pas';
      return;
    }
    this.loginService.register(this.username, this.password).subscribe({
  next: () => {
    this.message = 'Inscription réussie. Vous pouvez maintenant vous connecter.';
    this.router.navigate(['/login']);
  },
  error: (err) => {
    console.error('Erreur backend :', err); // ← pour voir l’erreur exacte
    this.message = 'Erreur lors de l\'inscription. Essayez à nouveau.';
  }
});

  }
}
<div class="container mt-5">
  <h2>Inscription</h2>
  <div class="form-group">
    <label>Nom d'utilisateur</label>
    <input class="form-control" placeholder="Nom d'utilisateur" [(ngModel)]="username" />
  </div>
  <div class="form-group mt-2">
    <label>Mot de passe</label>
    <input class="form-control" type="password" placeholder="Mot de passe" [(ngModel)]="password" />
  </div>
  <div class="form-group mt-2">
    <label>Confirmer le mot de passe</label>
    <input class="form-control" type="password" placeholder="Confirmer le mot de passe" [(ngModel)]="confirmPassword" />
  </div>
  <button class="btn btn-primary mt-3" (click)="register()">S'inscrire</button>
  <div class="text-danger mt-2">{{ message }}</div>
</div>
