import { Component } from '@angular/core';
import { LoginService } from '../service/login.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.scss']
})
export class RegisterComponent {
  username = '';
  password = '';
  confirmPassword = '';
  message = '';

  constructor(private loginService: LoginService, private router: Router) {}

  register() {
    if (this.password !== this.confirmPassword) {
      this.message = 'Les mots de passe ne correspondent pas';
      return;
    }
    this.loginService.register(this.username, this.password).subscribe({
  next: (res) => {
    console.log('Réponse du backend:', res); // ← important
    this.message = 'Inscription réussie. Vous pouvez maintenant vous connecter.';
    this.router.navigate(['/login']);
  },
  error: (err) => {
    console.error('Erreur backend :', err); // ← ici aussi
    this.message = 'Erreur lors de l\'inscription. Essayez à nouveau.';
  }
});

  }
}
<div class="d-flex justify-content-center align-items-center vh-100">
  <div class="card p-4 shadow" style="width: 400px;">
    <div class="text-center mb-3">
      <img src="assets/logo.png" alt="DEKRA" width="100" />
      <h3 class="mt-2">S'inscrire</h3>
    </div>

    <div class="form-group mb-3">
      <label for="username">Nom d'utilisateur</label>
      <div class="input-group">
        <span class="input-group-text">
          <i class="bi bi-person-fill"></i>
        </span>
        <input id="username" class="form-control" placeholder="Nom d'utilisateur" [(ngModel)]="username" />
      </div>
    </div>

    <div class="form-group mb-3">
      <label for="password">Mot de passe</label>
      <div class="input-group">
        <span class="input-group-text">
          <i class="bi bi-lock-fill"></i>
        </span>
        <input id="password" type="password" class="form-control" placeholder="Mot de passe" [(ngModel)]="password" />
      </div>
    </div>

    <div class="form-group mb-3">
      <label for="confirmPassword">Confirmer le mot de passe</label>
      <div class="input-group">
        <span class="input-group-text">
          <i class="bi bi-lock-fill"></i>
        </span>
        <input id="confirmPassword" type="password" class="form-control" placeholder="Confirmer le mot de passe" [(ngModel)]="confirmPassword" />
      </div>
    </div>

    <button class="btn btn-primary w-100" (click)="register()">S'inscrire</button>

    <div class="text-danger mt-2 text-center">{{ message }}</div>

    <div class="text-center mt-3">
      <small>&copy; 2025 DEKRA. Tous droits réservés. v1.0.0</small>
    </div>
  </div>
</div>
