export class UsersListComponent {
  constructor(crudService, fb) {
    this.crudService = crudService;
    this.fb = fb;
    this.Users = [];
    this.modalMode = 'add';
    this.selectedUserId = null;
    this.userForm = this.fb.group({
      firstName: [''],
      lastName: [''],
      email: ['']
    });
  }
  ngOnInit() {
    this.loadUsers();
  }
  loadUsers() {
    this.crudService.GetUsers().subscribe(res => {
      this.Users = res;
    });
  }
  openModal(mode, user) {
    this.modalMode = mode;
    this.selectedUserId = user?.id || null;
    if (user) {
      this.userForm.patchValue(user);
    } else {
      this.userForm.reset();
    }
    const modalEl = document.getElementById('userModal');
    const modal = new window.bootstrap.Modal(modalEl);
    modal.show();
  }
  closeModal() {
    const modalEl = document.getElementById('userModal');
    const modal = window.bootstrap.Modal.getInstance(modalEl);
    modal.hide();
  }
  onSubmit() {
    if (this.modalMode === 'add') {
      this.crudService.AddUser(this.userForm.value).subscribe(res => {
        this.Users.push(res);
        this.closeModal();
      });
    } else if (this.modalMode === 'edit' && this.selectedUserId) {
      this.crudService.updateUser(this.selectedUserId, this.userForm.value).subscribe(() => {
        this.loadUsers();
        this.closeModal();
      });
    }
  }
  delete(id, index) {
    if (confirm('Voulez-vous supprimer cet utilisateur ?')) {
      this.crudService.deleteUser(id).subscribe(() => {
        this.Users.splice(index, 1);
      });
    }
  }
}
