import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = '.Net + SQL Server + Angular 15 CRUD Application';
  isDarkMode = false;
  toggleDarkMode() {
    this.isDarkMode = !this.isDarkMode;
    document.body.classList.toggle('dark-mode', this.isDarkMode);
  }
}

<div class="container mt-3">
  <div class="d-flex justify-content-end">
    <button class="btn btn-sm btn-outline-secondary" (click)="toggleDarkMode()">
      {{ isDarkMode ? '‚òÄÔ∏è Light Mode' : 'üåô Dark Mode' }}
    </button>
  </div>
</div>

<router-outlet></router-outlet>
<nav class="navbar navbar-expand-sm bg-success navbar-dark">
  <!-- Links -->
  <ul class="navbar-nav">
    <li class="nav-item">
      <a class="nav-link" routerLinkActive="active" routerLink="/users-list"
      >Show Users</a>
    </li>
    <li class="nav-item">
      <a class="nav-link" routerLinkActive="active" routerLink="/add-user"
      >Add Users</a
    >
    </li>
  </ul>
</nav>
<div class="container">
  <router-outlet></router-outlet>
</div>

