export class UsersListComponent {
  constructor(crudService) {
    this.crudService = crudService;
    this.Users = [];
    this.searchText = '';
    this.sortColumn = '';
    this.sortDirection = 'asc';
    // Pagination
    this.currentPage = 1;
    this.itemsPerPage = 10;
  }
  ngOnInit() {
    this.crudService.GetUsers().subscribe(res => {
      this.Users = res;
    });
  }
  delete(id, i) {
    if (window.confirm('Do you want to go ahead?')) {
      this.crudService.deleteUser(id).subscribe(() => {
        this.Users.splice(i, 1);
      });
    }
  }
  sortBy(column) {
    if (this.sortColumn === column) {
      this.sortDirection = this.sortDirection === 'asc' ? 'desc' : 'asc';
    } else {
      this.sortColumn = column;
      this.sortDirection = 'asc';
    }
  }
  get paginatedUsers() {
    let filtered = this.Users.filter(user => Object.values(user).join(' ').toLowerCase().includes(this.searchText.toLowerCase()));
    if (this.sortColumn) {
      filtered = filtered.sort((a, b) => {
        const aVal = a[this.sortColumn]?.toString().toLowerCase();
        const bVal = b[this.sortColumn]?.toString().toLowerCase();
        return this.sortDirection === 'asc' ? aVal.localeCompare(bVal) : bVal.localeCompare(aVal);
      });
    }
    const start = (this.currentPage - 1) * this.itemsPerPage;
    return filtered.slice(start, start + this.itemsPerPage);
  }
  get totalPages() {
    const filteredLength = this.Users.filter(user => Object.values(user).join(' ').toLowerCase().includes(this.searchText.toLowerCase())).length;
    return Math.ceil(filteredLength / this.itemsPerPage);
  }
  changePage(page) {
    if (page >= 1 && page <= this.totalPages) {
      this.currentPage = page;
    }
  }
}
