import { Component } from '@angular/core';

interface User {
  id: number;
  name: string;
  email: string;
}

@Component({
  selector: 'app-user-list',
  templateUrl: './user-list.component.html',
  styleUrls: ['./user-list.component.scss']
})
export class UserListComponent {
  users: User[] = [
    { id: 1, name: 'John Doe', email: 'john.doe@example.com' },
    { id: 2, name: 'Jane Smith', email: 'jane.smith@example.com' },
    { id: 3, name: 'Michael Brown', email: 'michael.brown@example.com' },
    { id: 4, name: 'Emily Johnson', email: 'emily.johnson@example.com' }
  ];

  searchText = '';
  sortColumn: keyof User = 'id';
  sortDirection: 'asc' | 'desc' = 'asc';

  filteredUsers(): User[] {
    let filtered = this.users.filter(user =>
      user.name.toLowerCase().includes(this.searchText.toLowerCase()) ||
      user.email.toLowerCase().includes(this.searchText.toLowerCase())
    );

    return filtered.sort((a, b) => {
      const valueA = a[this.sortColumn];
      const valueB = b[this.sortColumn];
      const comparison = typeof valueA === 'string'
        ? valueA.localeCompare(valueB as string)
        : (valueA as number) - (valueB as number);

      return this.sortDirection === 'asc' ? comparison : -comparison;
    });
  }

  sortBy(column: keyof User): void {
    if (this.sortColumn === column) {
      this.sortDirection = this.sortDirection === 'asc' ? 'desc' : 'asc';
    } else {
      this.sortColumn = column;
      this.sortDirection = 'asc';
    }
  }

  sortIcon(column: keyof User): string {
    if (this.sortColumn !== column) return '';
    return this.sortDirection === 'asc' ? '&#9650;' : '&#9660;';
  }

  viewUser(user: User): void {
    alert(`Détails de l'utilisateur:\n${JSON.stringify(user, null, 2)}`);
  }

  editUser(user: User): void {
    alert(`Modifier l'utilisateur:\n${JSON.stringify(user, null, 2)}`);
  }

  deleteUser(user: User): void {
    if (confirm(`Supprimer ${user.name} ?`)) {
      this.users = this.users.filter(u => u.id !== user.id);
    }
  }

  addUser(): void {
    const id = this.users.length + 1;
    const newUser: User = {
      id,
      name: `Nouvel Utilisateur ${id}`,
      email: `nouveau${id}@exemple.com`
    };
    this.users.push(newUser);
  }
}
