import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = '.Net + SQL Server + Angular 15 CRUD Application';
  isDarkMode = false;
  toggleDarkMode() {
    this.isDarkMode = !this.isDarkMode;
    document.body.classList.toggle('dark-mode', this.isDarkMode);
  }
}
<nav class="navbar navbar-expand-sm bg-success navbar-dark">
  <!-- Links -->
  <div style="text-align: right; margin: 10px;">
  <button (click)="toggleDarkMode()" class="btn btn-outline-secondary">
    {{ isDarkMode ? '‚òÄÔ∏è Mode Clair' : 'üåô Mode Sombre' }}
  </button>
</div>

<h1>{{ title }}</h1>
<!-- Le reste de ton contenu ici -->

  <ul class="navbar-nav">
    <li class="nav-item">
      <a class="nav-link" routerLinkActive="active" routerLink="/users-list"
      >Show Users</a>
    </li>
    <li class="nav-item">
      <a class="nav-link" routerLinkActive="active" routerLink="/add-user"
      >Add Users</a
    >
    </li>
  </ul>
</nav>
<div class="container">
  <router-outlet></router-outlet>
</div>

